import React, {useState} from 'react';
import "./ResetButton.css";

const ResetButton = () =>
{

    const clearStorage = () =>
    {
        localStorage.clear();
    }

    return (
        <button class="reset-button" onClick={() => clearStorage()}>Reset App and Credentials</button>
    )
}

export default ResetButton;
